	core.scriptManager.addEvents("Sales Invoice", {
		form_setup: (core, doc) => {
			if (doc.docstatus !== 1) return;

			let promises = [];
			for (const dt of relations) {
				const filter = {};
				filter[dt.filter] = doc.name;
				promises.push(
					core.call({
						cmd: "renovation_erpnext.query",
						filters: filter,
						fields: ['DISTINCT ' + dt.select],
						doctype: dt.search_dt
					})
				);
			}

			Promise.all(promises)
			.then(p => {

				const result = [];
				for (let i = 0; i < p.length; i++) {
					// ignore failures
					const r = p[i];
					const d = relations[i];

					if (r.success && r.data.message && r.data.message.length > 0) {
						// filtering is necessary since NULL is possible
						const docs = r.data.message.map(f => { return {doctype: d.dt, name: f[d.select]}; }).filter(f => f.name);
						if (docs && docs.length > 0) {
							result.push({doctype: d.dt, docs: docs})
						}
					}
				}
				makeDash(result);
			});
		}
	});
}

