// Sales Invoice Make
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Sales Invoice - Make Mapped Doc";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	}
	let showMessage = (txt, ind) => {
		core.messages.next({message: txt, indicator: ind})
	}
	core.scriptManager.addEvents("Sales Invoice", {
		form_setup: (core, doc) => {
			if (doc.docstatus !== 1) return;

			let is_delivered_by_supplier = false;
			is_delivered_by_supplier = doc.items.some(function(item){
				return item.is_delivered_by_supplier ? true : false;
			})

			// Sales Return
			if (doc.outstanding_amount >= 0 || Math.abs(doc.outstanding_amount) < doc.grand_total) {
				core.bus.post('form_add_custom_button', {
					label: "Return / Credit Note",
					group: 'Make',
					onclick: make_return
				});
			}

			// Delivery Note
			if (doc.update_stock !== 1) {
				// show Make Delivery Note button only if Sales Invoice is not created from Delivery Note
				let from_delivery_note = false;
				from_delivery_note = doc.items
				.some(function(item) {
					return item.delivery_note ? true : false;
				});

				if(!from_delivery_note && !is_delivered_by_supplier) {
					core.bus.post('form_add_custom_button', {
						label: "Delivery",
						group: 'Make',
						onclick: make_delivery
					});
				}
			}

			// Payment
			if(doc.outstanding_amount !== 0) {
				core.bus.post('form_add_custom_button', {
					label: 'Payment',
					group: 'Make',
					onclick: make_payment
				});
			}
		}
	});

	const open_mapped_doc = (method, doc) => {
		if (doc.__unsaved) {
			showMessage("Please save this document first", "red");
			return;
		}
		core.call({
			cmd: "frappe.model.mapper.make_mapped_doc",
			method: method,
			source_name: doc.name
		})
		.then(res => {
			if (!res.success) {
				showMessage("Failed to get mapped document", "red");
				return Promise.reject(["Faield to get mapped doc", res]);
			}
			const mappedDoc = res.data.message;
			core.model.addToLocals(mappedDoc);
			core.bus.post("navigate_router", ["/form", mappedDoc.doctype, mappedDoc.name]);
		})
		.catch(r => {
			consoleWarn(r);
		})
	}

	const make_return = (core, doc) => { open_mapped_doc("erpnext.accounts.doctype.sales_invoice.sales_invoice.make_sales_return", doc); };
	const make_delivery = (core, doc) => { open_mapped_doc("erpnext.accounts.doctype.sales_invoice.sales_invoice.make_delivery_note", doc); };
	const make_payment = (core, doc) => { // payment is a bit odd
		core.call({
			cmd: "erpnext.accounts.doctype.payment_entry.payment_entry.get_payment_entry",
			dt: doc.doctype,
			dn: doc.name
		})
		.then(res => {
			if (!res.success) {
				showMessage("Failed to get mapped document", "red");
				return Promise.reject(["Faield to get mapped payment doc", res]);
			}
			const mappedDoc = res.data.message;
			core.model.addToLocals(mappedDoc);
			core.bus.post("navigate_router", ["/form", mappedDoc.doctype, mappedDoc.name]);
		})
		.catch(r => {
			consoleWarn(r);
		})
	};
}

(core) => {
    core.scriptManager.addEvents("Sales Invoice", {
        list_setup: (core) => {

            core.bus.post("list_row_status", {
                fn: (core, doc) => {
					if (doc.docstatus == 0) {
						return {text: "Draft", indicator: "red"};
					} else if (doc.docstatus == 2) {
						return {text: "Cancelled", indicator: "red"};
					}
                    if (doc.is_return == 1) {
                        return {text: "Return", indicator: "grey"};
                    } else if (doc.outstanding_amount == 0) {
                        return {text: "Paid", indicator: "green"};
                    } else if (doc.outstanding_amount < 0) {
                        return {text: "Credit Note Issued", indicator: "grey"};
                    } else if (doc.outstanding_amount > 0 && new Date(doc.due_date) > new Date()) {
                        return {text: "Unpaid", indicator: "orange"};
                    } else if (doc.outstanding_amount > 0 && new Date(doc.due_date) < new Date()) {
						return {text: "Overdue", indicator: "red"}
					}
                },
                include_fields: [
                    "customer", "customer_name", "base_grand_total", "outstanding_amount", "due_date", "company",
					"currency", "is_return", "docstatus"
                ]
            });
        }
    });
}

__form_grid_templates.items:
{% var visible_columns = row.get_visible_columns(["item_code", "qty", "rate", "amount",
	"stock_uom", "uom", "discount_percentage", "warehouse"]); %}

{% if(!doc) { %}
	<div class="row">
		<div class="col-sm-6 col-xs-8">{%= __("Items") %}</div>
		<div class="col-sm-2 hidden-xs text-right">{%= __("Qty") %}</div>
		<div class="col-sm-2 hidden-xs text-right">{%= __("Rate") %}</div>
		<div class="col-sm-2 col-xs-4 text-right">{%= __("Amount") %}</div>
	</div>
{% } else { %}
	{% var visible_column_fieldnames = $.map(visible_columns, function(x, i) {return x.fieldname}); %}
	<div class="row">
		<div class="col-sm-6 col-xs-8">
			{% if(doc.warehouse) {
				var color = "grey",
					title = "Warehouse",
					actual_qty = (frm.doc.doctype==="Sales Order"
						? doc.projected_qty : doc.actual_qty);
                if(flt(frm.doc.per_delivered) < 100
                    && in_list(["Sales Order Item", "Delivery Note Item"], doc.doctype)) {
    				if(actual_qty != undefined) {
    					if(actual_qty >= doc.qty) {
    						var color = "green";
    						var title = "In Stock"
    					} else {
    						var color = "red";
    						var title = "Not In Stock"
    					}
    				}
                } %}
				<span class="pull-right" title="{%= title %}" style="margin-left: 10px;">
					<span class="indicator {{ color }}">
						{%= doc.warehouse %}
					</span>
				</span>
			{% } %}

			{% if(in_list(["Sales Order Item", "Purchase Order Item"],
				doc.doctype) && frm.doc.docstatus===1) {
				var delivered = doc.doctype==="Sales Order Item" ?
                                                doc.delivered_qty : doc.received_qty;
                                var pending = flt(doc.qty) - flt(delivered);
                                %}
                <span class="indicator {%= pending>0 ? "orange" : "green" %}">{%= doc.item_code %}</span>
			{% } else { %}
                <strong>{%= doc.item_code %}</strong>
            {% } %}

			{% if(doc.item_name != doc.item_code && in_list(visible_column_fieldnames, "item_name")) { %}
				{% if (doc.item_code) { %}<br>{% } %}
                {%= doc.item_name %}{% } %}

			{% $.each(visible_columns || [], function(i, df) { %}
	{% 	var val = doc.get_formatted(df.fieldname);
	if((df.fieldname !== "description" && df.fieldname !== "item_name") && val) { %}
		<div class="row">
			<div class="col-xs-4 ellipsis">
				<strong title="{%= __(df.label) %}">{%= __(df.label) %}:</strong>
			</div>
			<div class="col-xs-8">
				{%= doc.get_formatted(df.fieldname) %}
			</div>
		</div>
	{% } else if (df.fieldname==="description") { %}
		<p>{{ doc.description }}</p>
	{% } %}
{% }); %}

		</div>

		<!-- qty -->
		<div class="col-sm-2 hidden-xs text-right">
			{%= doc.get_formatted("qty") %}
            <span class="small">{%= doc.uom || doc.stock_uom %}</span>
		</div>

		<!-- rate -->
		<div class="col-sm-2 hidden-xs text-right">
			{% if (!frappe.perm.is_visible("rate", doc, frm.perm)) { %}
				<span class="text-muted">{%= __("hidden") %}</span>
			{% } else { %}
				{%= doc.get_formatted("rate") %}
				{% if(doc.discount_percentage) { %}
				<br><span class="label label-default pull-right"
					title="{%= __("Discount")  %}">
					{%= -1 * doc.discount_percentage %}%</span>
				{% }%}
			{% } %}
		</div>

		<!-- amount -->
		<div class="col-sm-2 col-xs-4 text-right">
			{% if (!frappe.perm.is_visible("amount", doc, frm.perm)) { %}
				<span class="text-muted">{%= __("hidden") %}</span>
			{% } else { %}
				{%= doc.get_formatted("amount") %}
			{% } %}

			{% if (frappe.perm.is_visible("rate", doc, frm.perm)) { %}
			<div class="visible-xs text-muted">
				{%= doc.get_formatted("qty") %} <small>{%= doc.uom || doc.stock_uom %}</small>
				x {%= doc.get_formatted("rate") %}
			</div>
			{% } %}
		</div>
	</div>
{% } %}

