// ITEM _ EDIT STOCK
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Item - Edit Stock";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	};

	core.scriptManager.addEvents("Item", {
		form_setup: (core, parentdoc) => {
			if (parentdoc.__islocal || parentdoc.__unsaved) {
				return;
			}
			core.bus.post('form_add_custom_button', {
				label: "Edit Stocks",
				onclick: (core, doc) => {
					/*
					Show a modal with a warehouse Link
					on selection, fetch all variants, and stocks and rate for these
					*/
					const showSpinner = () => core.bus.post('spinner_service', {show: true});
					const hideSpinner = () => core.bus.post('spinner_service', {show: false});

					let _id = "item_edit_stocks";
					core.bus.post('show_dialog', {
						id: _id,
						fields: [
							{label: 'Warehouse', fieldname: 'warehouse', fieldtype: 'Link', reqd: 1, options: 'Warehouse'},
							{fieldname: 'items', fieldtype: 'Table', options: '__custom',
								customChildMeta: {
									doctype: '__custom',
									fields: [
										{label: 'Item Code', fieldname: 'item_code', fieldtype: 'Data', reqd: 1, inListView: 1},
										{label: 'Qty', fieldname: 'qty', fieldtype: 'Float', reqd: 1, inListView: 1},
										{label: 'Valuation Rate', fieldname: 'valuation_rate', fieldtype: 'Currency', reqd: 1, inListView: 1}
									]
								}
							}
						],
						events: {
							warehouse: (core, doc) => {
								doc.items = [];

								let itemCodes = [];
								new Promise((res, rej) => {
									if (parentdoc.has_variants) {
										console.log("Fetching variants");
										core.model.getList("Item", ["name"], {variant_of: parentdoc.name}, "item_code", 0, 1000)
										.then((response) => {
											if (!response.success) {
												core.messages.next({
													message: 'Failed fetching all the variants of this template. Please try again',
													indicator: 'red'
												});
												return Promise.reject(["Failed fetching all variants", response]);
											}

											itemCodes = response.data.map((i) => i.name);
											console.log("Variants", itemCodes);
											res(itemCodes);
										})
									} else {
										itemCodes = [parentdoc.name];
										res(itemCodes);
									}
								})
								.then(() => {

									// now for each variant, get stock
									let promises = [];
									showSpinner();
									for (const itemCode of itemCodes) {
										promises.push(
											new Promise((res, rej) => {
												core.call({
													// TODO: Write test cases for these parameters & fn
													cmd: "erpnext.stock.utils.get_stock_balance",
													"item_code": itemCode,
													"warehouse": doc.warehouse,
													"with_valuation_rate": true
												}).then((r) => {
													if (r.success && r.data && r.data.message && r.data.message.length == 2) {
														doc.items.push({
															item_code: itemCode,
															valuation_rate: r.data.message[1],
															qty: r.data.message[0]
														});
													}
													res();
												})
											})
										);
									}
									return Promise.all(promises)
								})
								.then(_ => {
									hideSpinner();
									core.ui.refreshField(_id, "items");
								})
								.catch(r => {
									core.messages.next({
										message: 'Failed fetching stock information in given warehouse. Please try again',
										indicator: 'red'
									});
									consoleWarn(r);
								});
							}
						},
						primary_button: {
							label: "Update",
							onclick: (core, doc, close_dialog) => {
								showSpinner();
								let s = {};
								core.model.newDoc("Stock Reconciliation").then((res) => {
									s = res;
									s.items = [];
									for (const c of doc.items) {
										s.items.push({
											warehouse: doc.warehouse,
											item_code: c.item_code,
											valuation_rate: c.valuation_rate,
											qty: c.qty
										})
									}
									console.log("Stock Reconciliation", s);
									return core.model.saveDoc(s);
								})
								.then(res => {
									if (!res.success) {
										core.messages.next({
											message: 'Failed saving new Stock Reconciliation. Please try again',
											indicator: 'red'
										});
										hideSpinner();
										return Promise.reject(["Failed saveDoc of Stock Reconciliation", res]);
									}
									return core.model.submitDoc(res.data);
								})
								.then(res => {
									hideSpinner();
									if (!res.success) {
										core.messages.next({
											message: 'Failed submitting new Stock Reconciliation. You may have to delete <a href="/#/Form/Stock Reconciliation/'+ encodeURIComponent(s.name) +'">'+ s.name +'</a> manually. Please try again',
											indicator: 'red'
										});
										return Promise.reject(["Failed SR Submit", res]);
									}
									core.messages.next({
										message: 'Successfully submitted new Stock Reconciliation <a href="/#/form/Stock Reconciliation/'+ encodeURIComponent(s.name) +'">'+ s.name +'</a>',
										indicator: 'green'
									});
									close_dialog();
								})
								.catch(r => {
									consoleWarn(r);
									hideSpinner();
								});
							}
						}
					});
				}
			});
		}
	});
}

// Variants in Item Dashboard
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Item - Show Variants in Dashboard";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	};

	const deleteVariant = (variant) => {
		if (confirm('Are you sure to delete this variant ?')) {
			core.bus.post('spinner_service', {show: true});
			core.model.deleteDoc('Item', variant)
			.then((response) => {
				core.bus.post('spinner_service', {show: false});
				if (response.success) {
					core.messages.next({indicator: 'green', message: 'Successfully deleted the variant'});
					location.reload();
				} else {
					// core will show message
				}
			})
		}
	};

	core.scriptManager.addEvents("Item", {
		form_setup: (core, doc) => {

			/*
			Proceed if current doc.has_variants only
			getList of Variants
			loop and generate an html and show.
			*/

			// get list of variants
			if (!doc.has_variants) {
				return;
			}

			core.model.getList("Item", ["name"], {variant_of: doc.name}, "item_code", 0, 1000)
			.then((response) => {
				if (!response.success) {
					core.messages.next({
						message: 'Failed fetching all the variants of this template. Please try again',
						indicator: 'red'
					});
					return Promise.reject(["Failed fetching all variants of this template", response]);
				}
				if (!response.data || response.data.length === 0) {
					return;
				}

				let variants = response.data.map((i) => i.name);
				if (!variants || variants.length === 0) {
					return; // nevermind; dont reject
				}

				const dash = {
					header: [
						{type: 'text', text: 'Variants', class: 'title'}
					],
					itemsMaxHeight: '250px',
					items: variants,
					itemsTemplate: [
						{type: 'link', link: (item) => ['/form/Item', item], class: 'flex-grow-1 text-secondary small'},
						{type: 'icon-btn', icon: 'nb-close', onclick: (core, item) => {
							deleteVariant(item);
						}}
					]
				};

				core.bus.post('form_dashboard_item', dash);
			})
			.catch(e => {
				consoleWarn(e);
			});
		}
	});
}

// Item - Pricing Rule
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Item - Pricing Rule";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	};

	const showSpinner = () => core.bus.post('spinner_service', {show: true});
	const hideSpinner = () => core.bus.post('spinner_service', {show: false});
	const showMessage = (message, indicator) => {
		core.messages.next({message: message, indicator: indicator});
	}

	let pricing_rule_meta = null;
	let targetItemCodes = []; // if length == 1 ? show single table : accordion // variants or normal..
	core.scriptManager.addEvents("Item", {
		form_setup: (core, doc) => {
			if (doc.__islocal || doc.__unsaved) {
				return;
			}

			const showSpinner = () => core.bus.post('spinner_service', {show: true});
			const hideSpinner = () => core.bus.post('spinner_service', {show: false});

			// get Pricing Rule meta
			// check permissions for all (loads meta along)
			core.perm.hasPerms("Pricing Rule", ["read", "write", "create", "delete"])
			.then(p => {
				if (p) {
					return core.meta.getDocMeta("Pricing Rule");
				} else {
					return Promise.reject("Not enough permissions for Pricing Rule");
				}					
			})
			.then((metaR) => {
				if (metaR.success) {
					pricing_rule_meta = metaR.data;
					add_button();
				} else {
					return Promise.reject("Failed loading Pricing Rule docmeta; User has enough Permissions");
				}
			})
			.then(() => {

			})
			.catch((e) => consoleWarn(e));
		}
	});
	let _doc = {};
	const existingPricingRules = [];
	const add_button = () => {
		// perms validated, meta loaded
		core.bus.post('form_add_custom_button', {
			label: 'Pricing Rule',
			onclick: on_pricing_rule_buttonClick
		});
	};

	const on_pricing_rule_buttonClick = (core, doc) => {
		// load variants
		// load targetItemCodes
		if (doc.has_variants) {
			// return so it chains to catch below
			return core.model.getList("Item", ["name"], {variant_of: doc.name}, "item_code", 0, 1000)
			.then(r => {
				if (r.success) {
					targetItemCodes = r.data.map(i => i.name);
					showDialog();
				} else {
					return Promise.reject("Failed loading Variants data");
				}
			})
		} else {
			targetItemCodes = [doc.name];
			showDialog();
		}

	}

	const showDialog = () => {
		// show layout dialog with custom meta
		_doc = {};
		showSpinner();

		const promises = [];
		for (const item_code of targetItemCodes) {
			promises.push(getApplicablePricingRules(item_code))
		}
		Promise.all(promises)
		.then(r => {
			hideSpinner();
			if (r.some(f => !f.success)) {
				console.log(r);
				return Promise.reject(["Failed getting existing details for items", r.find(f => !f.success).data]);
			}

			const targetDocs = [];
			// same docname is possible... so all objects SHOULD share same doc....
			// addToLocals first, fetch from there to have same reference
			for (let i = 0; i < targetItemCodes.length; i++) {

				const rules = [];
				// Promises were appended in order
				// Hope results come in order too :p
				if (r[i].data.message) // necessary, since message is empty when there is no pricing rule
					for (const doc of r[i].data.message) {
						// mandatory
						doc.doctype = "Pricing Rule";
						doc.__islocal = 0;

						core.model.addToLocals(doc);
						rules.push(doc.name);
						if (existingPricingRules.indexOf(doc.name) < 0) // Set
							existingPricingRules.push(doc.name);
					}
				targetDocs.push(rules);
			}

			// pass ref targetDocs
			for (let i = 0; i < targetItemCodes.length; i++) {
				const item_code = targetItemCodes[i];
				_doc[item_code] = [];
				for (const docname of targetDocs[i]) {
					_doc[item_code].push(core.model.getFromLocals("Pricing Rule", docname));
				}
			}
		})
		.then(() => {
			core.bus.post("show_dialog", {
				id: 'item_pricing_rule',
				title: 'Pricing Rules',
				doc: _doc,
				fields: getCustomLayoutFields(),
				primary_button: {
					label: "Save",
					onclick: updatePricingRules
				}
			});
		})
		.catch(e => consoleWarn(e));
	}

	const getApplicablePricingRules = (item_code) => {
		return core.call({
			cmd: "renovation_erpnext.renovation_pos.item.get_pricing_rules_on_item",
			item_code: item_code
		});
	}

	const updatePricingRules = (core, doc, close_dialog) => {
		showSpinner();

		// save current
		// delete removed

		let promises = [];
		let savingDocs = [];
		for (const item_code of targetItemCodes) {
			for (const d of doc[item_code]) {
				// dont want to resave already saving doc
				if (!d.__islocal && savingDocs.indexOf(d.name) >= 0) continue;

				promises.push(core.model.saveDoc(d));
				if (!d.__islocal) {
					savingDocs.push(d.name);
				}
			}
		}

		// docs to delete
		for (const docname of existingPricingRules.filter(i => savingDocs.indexOf(i) < 0)) {
			promises.push(core.model.deleteDoc("Pricing Rule", docname));
		}

		Promise.all(promises)
		.then(rr => {
			hideSpinner();
			if (rr.some(r => !r.success)) {
				// some or all failed
				if (rr.every(r => !r.success)) {
					// all fail
					showMessage("ALL the updation failed. Please try again later", "red");
				} else {
					// some fail
					showMessage("SOME of the updation failed. Please try again later", "red");
				}
			} else {
				// all success
				close_dialog();
				showMessage("Everything updated successfully", "green");
			}
		}); // no catch, everyone SHOULD resolve
	}

	/*
	Returns the layout for show_dialog
	Based on targetItemCodes.length, it decides if to use accordion
	*/
	const getCustomLayoutFields = () => {
		if (targetItemCodes.length === 1) {
			return [{
				fieldname: targetItemCodes[0],
				fieldtype: 'Table',
				options: 'Pricing Rule'
			}];
		} else {
			const fields = [];
			for (const item_code of targetItemCodes) {
				fields.push({
					label: item_code,
					fieldtype: 'Section Break',
					collapsible: 1
				});
				fields.push({
					fieldname: item_code,
					fieldtype: 'Table',
					options: 'Pricing Rule'
				});
			}
			return fields;
		}
	}
}

