// ITEM - Edit Prices
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Item - Edit Prices";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	};
	core.scriptManager.addEvents("Item", {
		form_setup: (core, doc) => {
			if (doc.__islocal || doc.__unsaved) {
				return;
			}
			core.bus.post('form_add_custom_button', {
				label: "Edit Prices",
				onclick: (core, doc) => {
					/*
					// OnClicking Edit Prices
					// Check if current item is a Template, a Variant or a normal Item
					// IF Template:
					//		Get all the variants of this and make sections
					//		With tables that will have entries for Item Prices
					// IF Variant, or if normal Item
					//		Get variant_of and treat it 
					*/

					const showSpinner = () => core.bus.post('spinner_service', {show: true});
					const hideSpinner = () => core.bus.post('spinner_service', {show: false});
					showSpinner();

					let fields = [];
					let _doc = {};
					let _itemCodes = [];

					let parseItemWisePrice_forDoc = (priceDict) => {
						/*
						"Item A":
							"Standard Selling": 10

						{
							"Item A": [
								{
									price_list: "Standard Selling",
									rate: 10
								}
							]
						}
						*/
						let doc = {};
						if (!priceDict)
							return doc;
						for (const itemCode in priceDict) {
							doc[itemCode] = [];
							for (const priceList in priceDict[itemCode]) {
								doc[itemCode].push(
									{
										price_list: priceList,
										rate: priceDict[itemCode][priceList]
									}
								)
							}
						}
						return doc;
					};

					let getTableField = (item_code) => {
						return {
							fieldname: item_code,
							fieldtype: 'Table',
							options: '__custom',
							customChildMeta: {
								doctype: '__custom',
								fields: [
									{label: 'Price List', fieldname: 'price_list', fieldtype: 'Link', reqd: 1, options: 'Price List', inListView: 1},
									{label: 'Rate', fieldname: 'rate', fieldtype: 'Currency', reqd: 1, inListView: 1}
								]
							}
						}
					}

					let openModal = () => {
						core.bus.post('show_dialog', {
							id: 'item_edit_prices',
							title: 'Edit Prices',
							doc: _doc,
							fields: fields,
							primary_button: {
								label: 'Save',
								onclick: (core, doc, close_dialog) => {
									// extract from doc to standard format
									let prices = {};
									for (const itemCode of _itemCodes) {
										prices[itemCode] = {};
										if (doc[itemCode]) {
											for (const child of doc[itemCode]) {
												prices[itemCode][child.price_list] = child.rate;
											}
										}
									}
									console.log("Saving Prices", prices);
									showSpinner();
									core.erp.stock.item.setItemPrice(prices, true).then((response) => {
										if (response.success) {
											close_dialog();
										} else {
											core.messages.next({
												message: 'Something went wrong while saving the prices. Please try again',
												indicator: 'red'
											});
											consoleWarn("Set Item Prices", response);
										}
										hideSpinner();
									});
								}
							}
						});
						hideSpinner();
					};

					if (doc.has_variants) {
						// this is a template
						// get all the variants first
						core.model.getList("Item", ["name"], {variant_of: doc.name}, "item_code", 0, 1000)
						.then((response) => {
							if (!response.success) {
								core.messages.next({
									message: 'Failed fetching all the variants of this template. Please try again',
									indicator: 'red'
								});
								return Promise.reject(["Failed fetching all variants", response]);
							}

							if (!response.data || response.data.length === 0) {
								core.messages.next({
									message: 'This template doesnt have any variants to edit prices',
									indicator: 'red'
								});
								return Promise.reject(["Error expected", {}]);
							}

							_itemCodes = response.data.map((i) => i.name);

							// make fields for all these
							for (const itemCode of _itemCodes) {
								fields.push({
									label: itemCode,
									fieldtype: 'Section Break',
									collapsible: 1
								});
								fields.push(getTableField(itemCode));
							}

							console.log("Variants of this template:", _itemCodes);

							// got variants, get prices
							return core.erp.stock.item.getItemPrice(_itemCodes);
						}).then((response) => {
							if (!response.success) {
								core.messages.next({
									message: 'Failed fetching prices for the variants. Please try again',
									indicator: 'red'
								});
								return Promise.reject(["Failed fetching prices of variants", response]);
							}

							// got variants, their prices
							if (response.data) {
								_doc = parseItemWisePrice_forDoc(response.data);
							}
							openModal();
						})
						.catch(e => {
							let title = "Price for Variants";

							if (Array.isArray(e)) {
								title = e[0];
								e = [1];
							}
							consoleWarn(title, e);
							hideSpinner();
						});
					} else {
						// not a template. show this single item
						// a single table is enough for this
						fields.push(
