frappe.views.calendar["Sales Order"] = {
	field_map: {
		"start": "delivery_date",
		"end": "delivery_date",
		"id": "name",
		"title": "customer_name",
		"allDay": "allDay"
	},
	gantt: true,
	filters: [
		{
			"fieldtype": "Link",
			"fieldname": "customer",
			"options": "Customer",
			"label": __("Customer")
		},
		{
			"fieldtype": "Select",
			"fieldname": "delivery_status",
			"options": "Not Delivered\nFully Delivered\nPartly Delivered\nClosed\nNot Applicable",
			"label": __("Delivery Status")
		},
		{
			"fieldtype": "Select",
			"fieldname": "billing_status",
			"options": "Not Billed\nFully Billed\nPartly Billed\nClosed",
			"label": __("Billing Status")
		},
	],
	get_events_method: "erpnext.selling.doctype.sales_order.sales_order.get_events",
	get_css_class: function(data) {
		if(data.status=="Closed") {
			return "success";
		} if(data.delivery_status=="Not Delivered") {
			return "danger";
		} else if(data.delivery_status=="Partly Delivered") {
			return "warning";
		} else if(data.delivery_status=="Fully Delivered") {
			return "success";
		}
	}
}

renovation_scripts.code:
// Sales Order Related Doc
(core) => {

	const relations = [
		{dt: "Sales Invoice", search_dt: "Sales Invoice Item", select: "parent", filter: "sales_order"},
		{dt: "Delivery Note", search_dt: "Delivery Note Item", select: "parent", filter: "against_sales_order"},
		{dt: "Payment Entry", search_dt: "Payment Entry Reference", select: "parent", filter: "reference_name"},
		{dt: "Journal Entry", search_dt: "Journal Entry Account", select: "parent", filter: "reference_name"}
	];

	const dash = {
		header: [
			{type: 'text', text: 'Related Documents', class: 'title'}
		],
		itemsMaxHeight: '250px',
		items: [],
		itemsListClass: 'position-relative',
		itemsTemplate: [
			{type: 'text', class: 'faded small text-nowrap mr-1', property: 'doctype'},
			{type: 'loop', property: 'docs', class: 'position-absolute', style: {left: '5.2em'}, template: [
				{type: 'link', link: (item) => ['/form', item.doctype, item.name], property: 'name', class: 'text-secondary small text-nowrap ml-1 position'}
			]}
		]
	};

	const makeDash = (data) => {
		if (!data || data.length === 0) {
			dash.footer = [
				{type: 'text', text: 'No documents', class: 'text-center mt-1 h6 uppercase'}
			];
			dash.items = null;
		} else {
			dash.items = data;
		}
		console.log('Related Docs', data);
		core.bus.post('form_dashboard_item', dash);
	};


	core.scriptManager.addEvents("Sales Order", {
		form_setup: (core, doc) => {
			if (doc.docstatus !== 1) return;

			let promises = [];
			for (const dt of relations) {
				const filter = {};
				filter[dt.filter] = doc.name;
				promises.push(
					core.call({
						cmd: "renovation_erpnext.query",
						filters: filter,
						fields: ['DISTINCT ' + dt.select],
						doctype: dt.search_dt
					})
				);
			}

			Promise.all(promises)
			.then(p => {

				const result = [];
				for (let i = 0; i < p.length; i++) {
					// ignore failures
					const r = p[i];
					const d = relations[i];

					if (r.success && r.data.message && r.data.message.length > 0) {
						// filtering is necessary since NULL is possible
						const docs = r.data.message.map(f => { return {doctype: d.dt, name: f[d.select]}; }).filter(f => f.name);
						if (docs && docs.length > 0) {
							result.push({doctype: d.dt, docs: docs})
						}
					}
				}
				makeDash(result);
			});
		}
	});
}

// Sales Order - Close & Reopen
(core) => {
    core.scriptManager.addEvents("Sales Order", {
        form_setup: (core, doc) => {

			core.perm.hasPerm('Sales Order', 'submit')
			.then(yes => {
				if (!yes) return;

				addButton(doc);
			});
        }
    });

	const showSpinner = () => core.bus.post('spinner_service', {show: true});
	const hideSpinner = () => core.bus.post('spinner_service', {show: false});
	const showMessage = (message, indicator) => {
		core.messages.next({message: message, indicator: indicator});
	}

	const updateStatus = (status, name) => {
		showSpinner();
		core.call({
			cmd: "erpnext.selling.doctype.sales_order.sales_order.close_or_unclose_sales_orders",
			status: status,
			names: JSON.stringify([name])
		}).then(r => {
			hideSpinner();
			if (r.success) {
				showMessage("Reloading document", "green");
				setTimeout(() => location.reload(), 500);
			} else {
				showMessage("Something went wrong while changing the status", "red");
			}
		});
	};

	const addButton = (doc) => {

		if (doc.status !== "Closed" && (doc.per_delivered < 100 || doc.per_billed < 100)) {
			addCloseButton();
		} else if (doc.status === "Closed") {
			addReopenButton();
		}
	}

	const addCloseButton = () => {
		core.bus.post('form_add_custom_button', {
			label: 'Close Order',
			onclick: (core, doc) => {
				updateStatus("Closed", doc.name);
			}
		});
	};
	const addReopenButton = () => {
		core.bus.post('form_add_custom_button', {
			label: 'Re-Open Order',
			onclick: (core, doc) => {
				updateStatus("Draft", doc.name);
			}
		});
	};
}

// Sales Order Row Status
(core) => {
    core.scriptManager.addEvents("Sales Order", {
        list_setup: (core) => {

            core.bus.post("list_row_status", {
                fn: (core, doc) => {
					if (doc.docstatus == 0) {
						return {text: "Draft", indicator: "red"};
					} else if (doc.docstatus == 2) {
						return {text: "Cancelled", indicator: "red"};
					}
                    if (doc.status === "Closed") {
                        return {text: "Closed", indicator: "green"};

					} else if (doc.order_type !== "Maintenance"
						&& doc.per_delivered < 100 && new Date(doc.delivery_date) < new Date()) {
                        return {text: "Overdue", indicator: "red"};

					} else if (doc.order_type !== "Maintenance"
						&& doc.per_delivered < 100 && doc.status !== "Closed") {

						if (doc.per_billed < 100)
							return {text: "To Deliver and Bill", indicator: "orange"};
						else
							return {text: "To Deliver", indicator: "orange"};

					} else if (doc.order_type !== "Maintenance" && doc.per_billed < 100
						&& doc.per_delivered == 100 && doc.status !== "Closed") {
                        return {text: "To Bill", indicator: "orange"};
                    } else if ((doc.order_type === "Maintenance" || doc.per_delivered == 100)
							&& doc.per_billed == 100 && doc.status!=="Closed") {
						return {text: "Completed", indicator: "green"};
					}
                },
                include_fields: [
                    "base_grand_total", "customer_name", "currency", "delivery_date",
					"per_delivered", "per_billed", "status", "order_type", "name", "docstatus"
                ]
            });
        }
    });
}

// Sales Order Make
(core) => {
	const consoleWarn = (...logs) => {
		let error = "Sales Order - Make Mapped Doc";
		let processErrorArray = (array) => {
			for (let i = 0; i < array.length; i++) {
				let arg = array[i];
				if (Array.isArray(arg)) {
					processErrorArray(arg);
					continue;
				} else if (typeof arg !== "string")
					arg = JSON.stringify(arg);
				error += `\n${arg}`;
			}
		}
		processErrorArray(logs);
		console.warn(error);
	};
	let showMessage = (txt, ind) => {
		core.messages.next({message: txt, indicator: ind})
	}
	core.scriptManager.addEvents("Sales Order", {
		form_setup: (core, doc) => {

			// add these buttons if SUBMITTED ONLY
			if (doc.docstatus !== 1) return;

			let fn = (label, method, custom_onclick) => {
				core.bus.post('form_add_custom_button', {
					label: label,
					group: 'Make',
					onclick: custom_onclick ? custom_onclick : (core, doc) => {
						core.call({
							cmd: "frappe.model.mapper.make_mapped_doc",
							method: method,
							source_name: doc.name
						})
						.then(res => {
							if (!res.success) {
								showMessage("Failed to get mapped document", "red");
								return Promise.reject(["Faield to get mapped doc", res]);
							}
							const mappedDoc = res.data.message;
							core.model.addToLocals(mappedDoc);
							core.bus.post("navigate_router", ["/form", mappedDoc.doctype, mappedDoc.name]);
						})
						.catch(r => {
							consoleWarn(r);
						})
					}
				});
			}

			// sales invoice
			if (doc.per_billed < 100) {
				fn("Invoice", "erpnext.selling.doctype.sales_order.sales_order.make_sales_invoice");
			}

			// delivery note
			let allow_delivery = true; // can go false when Supplier drop ships?
			if(doc.per_delivered < 100 && ["Sales", "Shopping Cart"].indexOf(doc.order_type)!==-1 && allow_delivery) {
				fn("Delivery", "erpnext.selling.doctype.sales_order.sales_order.make_delivery_note");
			}

			// payment
			if (doc.per_billed === 0) {
				fn("Payment", "", (core, doc) => {
					core.call({
						cmd: "erpnext.accounts.doctype.payment_entry.payment_entry.get_payment_entry",
						dt: doc.doctype,
						dn: doc.name
					})
					.then(res => {
						if (!res.success) {
							showMessage("Failed to get mapped document", "red");
							return Promise.reject(["Faield to get mapped payment doc", res]);
						}
						const mappedDoc = res.data.message;
						core.model.addToLocals(mappedDoc);
						core.bus.post("navigate_router", ["/form", mappedDoc.doctype, mappedDoc.name]);
					})
					.catch(r => {
						consoleWarn(r);
					})
				});
			}
		}
	})
}

